mapper.select:
SELECT distinct m.* FROM sys_menu m LEFT JOIN sys_role_menu rm ON rm.menu_id = m.id
AND rm.role_id = #{roleId}
AND name LIKE CONCAT('%',#{menuName},'%')
AND level = #{level}
ORDER BY m.sort

SELECT distinct m.*
FROM sys_menu m
LEFT JOIN sys_role_menu n ON n.menu_id = m.id
WHERE n.role_id IN (
SELECT role_id
FROM sys_user_role
WHERE user_id = #{userId}
)
AND m.level = #{level}
ORDER BY m.sort
